class ServerOffloader {
	constructor(io, PathWorker, PhysicsWorker, WorldWorker) {
		this.helper = new OffloaderHelper(this);
		this.subscriberItems = {};
		this.io = io;
		this.servers = {};

		this.servers.path = PathWorker;
		this.servers.path.subscriberItems = {};
		this.servers.path.on("message", this.onMessage);
		this.servers.path.io = io;
		this.servers.path.offloader = this;

		this.servers.physics = PhysicsWorker;
		this.servers.physics.subscriberItems = {};
		this.servers.physics.on("message", this.onMessage);
		this.servers.physics.io = io;
		this.servers.physics.offloader = this;

		this.servers.world = WorldWorker;
		this.servers.world.subscriberItems = {};
		this.servers.world.on("message", this.onMessage);
		this.servers.world.io = io;
		this.servers.world.offloader = this;

		this.helper.worldUpdate("load"); //??
	}

	onMessage(data) {
		if (data.tick == true) {
			this.io.emit("response", data);
			return true;
		}

		if (data.server == "broadcast") {
			//console.log("!!!BROADCAST!!!");
			this.io.emit("response", data);
			return true;
		}

		if (data.server == "client") {
			this.io.to(data.socketId).emit("response", data);
			return true;
		}

		if (data.serverside == true) {
			//Don't immediately respond, call backs to other threads.
			//console.log("\n\n--SERVERSIDECOMMAND--\n"+JSON.stringify(data)+"\n--SERVERSIDECOMMAND\n\n");
			if (data.async == true) {
				this.offloader.doAction(data.creatorId, data.server, data.action, data.callback, data.data);
			} else {
				this.offloader.doAsyncAction(data.creatorId, data.server, data.action, data.callback, data.data);
			}
			return true;
		}
		if (this.subscriberItems[data.creatorId] == undefined) {
			//console.log("No subscriber?");
			return true;
		}
		if (this.subscriberItems[data.creatorId][data.action] !== undefined) {
			var callbackItem = this.subscriberItems[data.creatorId][data.action];
			callbackItem.callback(data);
		}
	}

	init() {
		//console.error(this.worker);
	}

	doAction(creator, server, action, callback, data) {
		/*
		console.log(
			"Do: " +
				action +
				" on " +
				server +
				"for " +
				creator +
				"\nData: " +
				JSON.stringify(data) +
				"\n"
		);
		*/

		if (!this.servers[server]) {
			console.error("No server to handle", data);
			return;
		}

		this.servers[server].postMessage({
			server: server,
			action: action,
			creatorId: creator,
			data: data
		});
	}

	doAsyncAction(creator, server, action, callback, data) {
		//console.log("Do: "+creator);

		this.servers[server].postMessage({
			async: true,
			server: server,
			action: action,
			creatorId: creator,
			data: data
		});
	}
}

class Settings {
	constructor(filesystem, filename) {
		this.fs = filesystem;
		this.filename = filename + ".json";

		//Defaults
		this.settings = {
			showTut: true,
			advancedFilters: false,
			firstLoad: true,
			particles: true,
			lights: true,
			bloom: true,
			damage: true,
			blood: true,
			fullscreen: false,
			performance: false,
			//If localmove is true, the game is aware of the drawmatrix in the world server.
			//It can improve performance (frame drops) if you disable this setting
			localmove: true,
			musicValue: 5,
			sfxValue: 10,

			/*KEYS*/
			key_Escape: "Escape",
			key_Space: "Space",
			key_KeyM: "KeyM",
			key_KeyI: "KeyI",

			key_F5: "F5",

			keymap_Escape: "key_Escape",
			enabled_mods: {}
		};
		this.defaults = JSON.parse(JSON.stringify(this.settings));
	}

	toJSON() {
		return this.settings;
	}

	init() {
		this.load();
		Howler.volume(this.settings.musicValue / 100);
		return new Proxy(this, {
			get(target, name, value, rec) {
				return typeof target.settings[name] == "undefined" ? target.defaults[name] : target.settings[name];
			},
			set(target, name, value, rec) {
				target.settings[name] = value;
				target.save();
				return Reflect.set(target, name, value, rec);
			}
		});
	}

	save() {
		Howler.volume(this.settings.musicValue / 100);
		this.fs.saveSync(this.filename, this.settings);
	}

	load() {
		if (this.fs.keyExists(this.filename)) {
			this.settings = this.fs.loadSync(this.filename);
		}
	}
}

